package chapter13;

public class Board {
	String title;
	String content;
	
	public Board(String title, String content) {
		// TODO Auto-generated constructor stub
		this.content = content;
		this.title = title;
	}

	/**
	 * @return the title
	 */
	public String getTitle() {
		return title;
	}

	/**
	 * @return the content
	 */
	public String getContent() {
		return content;
	}
	
	

}
